  }
}

class UserService {


  getUsers() {
    return axios.get(usersURL, config);
  }

  deleteUser(id) {
    axios.delete((usersURL + "/" + id), config);
  }

  postUser(name, position) {
    axios.post((usersURL + "?name=" + name + "&position=" + position), config);
  }
}

export default new UserService();
