import axios from "axios";
import { Buffer } from "buffer";

const userName = "restUser";
const userPassword = "restPassword";
const usersURL = 'http://localhost:8080/workers';
const token = userName + ":" + userPassword;


axios.interceptors.request.use(
  config =>
    config.headers.auth = {
      username: userName,
      password: userPassword
    }
)

class UserService {
  getUsers() {
    return axios.get(usersURL, {
      auth: {
        username: userName,
        password: userPassword
      },
    });
  }

  deleteUser(id) {
    axios.delete((usersURL + "/" + id), {
      auth: {
        username: userName,
        password: userPassword
      },
    });
  }

  postUser(name, position) {
    axios.post((usersURL + "?name=" + name + "&position=" + position), {
      auth: {
        username: userName,
        password: userPassword
      },
    });
  }
}

export default new UserService();
