import axios from "axios";

const usersURL = 'http://localhost:8080/workers';
const AUTH = {
  name: "restUser",
  password: "restPassword"
}

class UserService {


  getUsers() {
    return axios.get(usersURL, AUTH);
  }

  deleteUser(id) {
    axios.delete((usersURL + "/" + id), AUTH);
  }

  postUser(name, position) {
    axios.post((usersURL + "?name=" + name + "&position=" + position), AUTH);
  }
}

export default new UserService();
